mapper.update:
update core_AuthorYearFlag
set AutID = #{autid,jdbcType=VARCHAR},
  dr_AutName = #{drAutname,jdbcType=VARCHAR},
  YearID = #{yearid,jdbcType=INTEGER},
  Flag = #{flag,jdbcType=SMALLINT}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select ID, AutID, dr_AutName, YearID, Flag
from core_AuthorYearFlag
where ID = #{id,jdbcType=VARCHAR}
